import React from 'react';
import './style.css';

const Todo = props => {
    return(
       <li className="TodoList">
           <span> {props.task} onClick={()=>{
               props.updateTodo(props.id)}}</span>
           <button onClick={()=>{
               props.removeTodo(props.id)
           }} className="RemoveTodo-btn">X</button>
       </li>     
    );
}

export default Todo;
